import React, { Component } from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import {
  Dialog,
  Button,
  Grid,
  InputLabel,
  FormControl,
  MenuItem,
  Select,
  InputAdornment,
  Checkbox,
  TextField,
  DialogActions,
  FormControlLabel,
  DialogTitle,
  DialogContent,
  TablePagination,
  IconButton,
  Icon
} from "@material-ui/core";
import { Breadcrumb, ConfirmationDialog } from "egret";
import MaterialTable, {
  MTableToolbar,
  Chip,
  MTableBody,
  MTableHeader
} from "material-table";
import Autocomplete from '@material-ui/lab/Autocomplete';
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import { getUserByUsername, getUserByEmail, saveUser, addNewUser, getAllRoles, saveHealthOrgByUser, getListHealthOrgByUser } from "./EmployeeService";
import AsynchronousAutocomplete from "../utilities/AsynchronousAutocomplete";
import Draggable from 'react-draggable';
import Paper from '@material-ui/core/Paper';
import '../../../styles/views/_loadding.scss';
import clsx from 'clsx';
import CircularProgress from '@material-ui/core/CircularProgress';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../../../styles/views/_style.scss';
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3
});


function EmployeeDialog() {
  // const { useState } = React;

  const [columns, setColumns] = useState([
    { title: 'Name', field: 'name' },
    { title: 'Surname', field: 'surname', initialEditValue: 'initial edit value' },
    { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
    {
      title: 'Birth Place',
      field: 'birthCity',
      lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
    },
  ]);

  const [data, setData] = useState([
    { name: '', surname: '', birthYear: 1987, birthCity: 63 },
    { name: '', surname: '', birthYear: 2017, birthCity: 34 },
  ]);

  useEffect(() => {
    const getDataEployee = axios.get("http://training-api.oceantech.com.vn/cms/employees/search")
    console.log(getDataEployee)
  }, [])

  function Appp() {
    const getData = () => {
      axios.get("http://training-api.oceantech.com.vn/cms/employees/search").then((Response) => {
        console.log(Response)
      })
    }
    return <div className="">hehehe</div>
  }

  return (
    <MaterialTable
      title="Thông tin nhân viên"
      columns={columns}
      data={data}
      editable={{
        onRowAdd: newData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              setData([...data, newData]);
              
              resolve();
            }, 1000)
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataUpdate = [...data];
              const index = oldData.tableData.id;
              dataUpdate[index] = newData;
              setData([...dataUpdate]);

              resolve();
            }, 1000)
          }),
        onRowDelete: oldData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataDelete = [...data];
              const index = oldData.tableData.id;
              dataDelete.splice(index, 1);
              setData([...dataDelete]);
              
              resolve()
            }, 1000)
          }),
      }}
    />
  )
}

function Appp() {
  const getData = () => {
    axios.get("http://training-api.oceantech.com.vn/cms/employees/search").then((Response) => {
      console.log(Response)
    })
  }
  return <div className="">hehehe 
  <button onClick={getData}>get</button>
  </div>
}

// export default EmployeeDialog;
export default Appp;
