import React, { Component } from "react";
import {
  Grid,
  TextField,
  IconButton,
  Icon,
  Button,
  TableHead,
  TableCell,
  TableRow,
  Checkbox,
  TablePagination,
  Tooltip,
  FormControl,
  Input,
  InputAdornment,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import SearchIcon from "@material-ui/icons/Search";
import MaterialTable, {
  MTableToolbar,
  Chip,
  MTableBody,
  MTableHeader,
} from "material-table";
import { getAllEmployee, getEmployeeById,  } from "./EmployeeService";
import { Breadcrumb, ConfirmationDialog } from "egret";
import { useTranslation, withTranslation, Trans } from "react-i18next";
import shortid from "shortid";
import { saveAs } from "file-saver";
import { useEffect, useState } from "react";
import axios from "axios";
import { withStyles } from "@material-ui/core/styles";
import { toast } from "react-toastify";
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3,
});

const LightTooltip = withStyles((theme) => ({
  tooltip: {
    backgroundColor: theme.palette.common.white,
    color: "rgba(0, 0, 0, 0.87)",
    boxShadow: theme.shadows[1],
    fontSize: 11,
    position: "absolute",
    top: "-15px",
    left: "-30px",
    width: "80px",
  },
}))(Tooltip);

function MuiBtnAdd(props) {
  const { t, i18n } = useTranslation();
  const item = props.item;
  return (
    <div className="none_wrap">
      <LightTooltip
        title={t("general.text")}
        placement="right-end"
        enterDelay={300}
        leaveDelay={200}
      >
        <IconButton size="small" onClick={() => props.onSelect(item, 0)}>
          <Icon fontSize="small" color="primary">
            Thêm mới
          </Icon>
        </IconButton>
      </LightTooltip>
    </div>
  );
}

function MaterialButton(props) {
  const { t, i18n } = useTranslation();
  const item = props.item;
  return (
    <div className="none_wrap">
      <LightTooltip
        title={t("general.editIcon")}
        placement="right-end"
        enterDelay={300}
        leaveDelay={200}
      >
        <IconButton size="small" onClick={() => props.onSelect(item, 0)}>
          <Icon fontSize="small" color="primary">
            edit
          </Icon>
        </IconButton>
      </LightTooltip>
      <LightTooltip
        title={t("general.deleteIcon")}
        placement="right-end"
        enterDelay={300}
        leaveDelay={200}
      >
        <IconButton size="small" onClick={() => props.onSelect(item, 1)}>
          <Icon fontSize="small" color="error">
            delete
          </Icon>
        </IconButton>
      </LightTooltip>
    </div>
  );
}

function EmployeeInfo(props) {

  const state = {
    rowsPerPage: 10,
    page: 0,
    data: [],
    totalElements: 0,
    itemList: [],
    shouldOpenEditorDialog: false,
    shouldOpenConfirmationDialog: false,
    shouldOpenConfirmationDeleteAllDialog: false,
    keyword: "",
    shouldOpenNotificationPopup: false,
    Notification: "",
  };
  const [posts, setPosts] = useState([]);
  const { t, i18n } = useTranslation();

  useEffect(() => {
    getAllEmployee({ code: null, name: null })
      .then(res => {
        setPosts(res.data.data)
        console.log(res.data.data)
      })
      .catch(error => console.log(`Error: ${error}`));
  }, []);

  const handleDelete = () => {

  }


    let columns = [
      {
        title: ("Actions"),
        field: "custom",
        align: "left",
        width: "250",
        render: rowData => <MaterialButton item={rowData}
          onSelect={(rowData, method) => {
            if (method === 0) {
              getEmployeeById(rowData.id).then(({ data }) => {
                if (data.parent === null) {
                  data.parent = {};
                }
                this.setState({
                  item: data,
                  shouldOpenEditorDialog: true
                });
              })
            } else if (method === 1) {
              this.handleDelete(rowData.id);
            } else {
              alert('Call Selected Here:' + rowData.id);
            }
          }}
        />
      },
      { title: ("ID"), field: "id", width: "150" },
      { title: ("Name"), field: "name", width: "150" },
      { title: ("Age"), field: "age", width: "150" },
      { title: ("Code"), field: "code", width: "150" },
      { title: ("Province"), field: "province", width: "150" },
      { title: ("Email"), field: "email", align: "left", width: "150" },
      { title: ("Phone"), field: "phone", width: "150" },
    ];

  return (
    // <div className="">
    //   <MaterialTable
    //     title="Thông tin nhân viên"
    //     columns={columns}
    //     data={posts}
    //   />
    // </div>
    <div className="m-sm-30"> 
      <Grid container spacing={2} justify="space-between">
        <Grid item md={3} xs={12}>
          <Button
            className="align-bottom mr-16 mb-16"
            variant="contained"
            color="primary"
            onClick={() => this.handleEditItem(null)}
          >
            {t("general.add")}
          </Button>
          <Button
            className="align-bottom mb-16"
            variant="contained"
            color="primary"
            onClick={handleDelete()}
          >
            {t("general.delete")}
          </Button>

          {this.state.shouldOpenConfirmationDeleteAllDialog && (
            <ConfirmationDialog
              open={this.state.shouldOpenConfirmationDeleteAllDialog}
              onConfirmDialogClose={this.handleDialogClose}
              onYesClick={this.handleDeleteAll}
              title={t("confirm")}
              text={t('DeleteAllConfirm')}
              Yes={t('general.Yes')}
              No={t('general.No')}
            />
          )}
          
        </Grid>
        <Grid item md={4} sm={12} xs={12}>
          <FormControl fullWidth style={{ marginTop: "6px" }}>
            <Input
              className="search_box w-100"
              onChange={this.handleTextChange}
              onKeyDown={this.handleKeyDownEnterSearch}
              placeholder={t("general.enterKeyword")}
              id="search_box"
              startAdornment={
                <InputAdornment>
                  <Link>
                    {" "}
                    <SearchIcon
                      // onClick={() => this.search(keyword)}
                      style={{ position: "absolute", top: "0", right: "0" }}
                    />
                  </Link>
                </InputAdornment>
              }
            />
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <div>
            {/* {this.state.shouldOpenEditorDialog && (
              <AgencyDialog
                t={t}
                i18n={i18n}
                handleClose={this.handleDialogClose}
                open={this.state.shouldOpenEditorDialog}
                handleOKEditClose={this.handleOKEditClose}
                item={this.state.item}
              />
            )}

             */}

            {this.state.shouldOpenConfirmationDialog && (
              <ConfirmationDialog
                title={t("general.confirm")}
                open={this.state.shouldOpenConfirmationDialog}
                onConfirmDialogClose={this.handleDialogClose}
                onYesClick={this.handleConfirmationResponse}
                // title={t("confirm")}
                text={t('DeleteConfirm')}
                Yes={t('general.Yes')}
                No={t('general.No')}
              />
            )}
          </div>
          <MaterialTable
            title={t("general.list")}
            data={this.state.itemList}
            columns={columns}
            //parentChildData={(row, rows) => rows.find(a => a.id === row.parentId)}
            parentChildData={(row, rows) => {
              var list = rows.find((a) => a.id === row.parentId);
              return list;
            }}
            localization={{
              body: {
                emptyDataSourceMessage: `${t(
                  "general.emptyDataMessageTable"
                )}`,
              },
              toolbar: {
                // nRowsSelected: `${t('general.selects')}`,
                nRowsSelected: `${t("general.selects")}`,
              },
            }}
            options={{
              selection: true,
              actionsColumnIndex: -1,
              paging: false,
              search: false,
              rowStyle: (rowData) => ({
                backgroundColor:
                  rowData.tableData.id % 2 === 1 ? "#EEE" : "#FFF",
              }),
              maxBodyHeight: "1000px",
              minBodyHeight: "370px",
              headerStyle: {
                backgroundColor: "#358600",
                color: "#fff",
              },
              padding: "dense",
              toolbar: false,
            }}
            components={{
              Toolbar: (props) => <MTableToolbar {...props} />,
            }}
            onSelectionChange={(rows) => {
              this.data = rows;
            }}
            // actions={[
            //   {
            //     tooltip: 'Remove All Selected Users',
            //     icon: 'delete',
            //     onClick: (evt, data) => {
            //       this.handleDeleteAll(data);
            //       console.log(data)
            //       alert('You want to delete ' + data.length + ' rows');
            //     }
            //   },
            // ]}
          />
          <TablePagination
            align="left"
            className="px-16"
            rowsPerPageOptions={[1, 2, 3, 5, 10, 25]}
            component="div"
            labelRowsPerPage={t("general.rows_per_page")}
            labelDisplayedRows={({ from, to, count }) =>
              `${from}-${to} ${t("general.of")} ${
                count !== -1 ? count : `more than ${to}`
              }`
            }
            count={this.state.totalElements}
            rowsPerPage={this.state.rowsPerPage}
            page={this.state.page}
            backIconButtonProps={{
              "aria-label": "Previous Page",
            }}
            nextIconButtonProps={{
              "aria-label": "Next Page",
            }}
            onChangePage={this.handleChangePage}
            onChangeRowsPerPage={this.setRowsPerPage}
          />
        </Grid>
      </Grid>
    </div>
  );
}

export default EmployeeInfo;
