import React, { Component } from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import {
  Dialog,
  Button,
  Grid,
  InputLabel,
  FormControl,
  MenuItem,
  Select,
  InputAdornment,
  Checkbox,
  TextField,
  DialogActions,
  FormControlLabel,
  DialogTitle,
  DialogContent,
  TablePagination,
  IconButton,
  Icon,
} from "@material-ui/core";
import { Breadcrumb, ConfirmationDialog } from "egret";
import MaterialTable, {
  MTableToolbar,
  Chip,
  MTableBody,
  MTableHeader,
} from "material-table";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import {
  getAllEmployee,
  getUserByEmail,
  saveUser,
  addNewUser,
  getAllRoles,
  saveHealthOrgByUser,
  getListHealthOrgByUser,
} from "./EmployeeService";
import AsynchronousAutocomplete from "../utilities/AsynchronousAutocomplete";
import Draggable from "react-draggable";
import Paper from "@material-ui/core/Paper";
import "../../../styles/views/_loadding.scss";
import clsx from "clsx";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../../../styles/views/_style.scss";
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3,
});

function EmployeeDialog() {
  // const { useState } = React;

  const [columns, setColumns] = useState([
    { title: "Name", field: "name" },
    {
      title: "Surname",
      field: "surname",
      initialEditValue: "initial edit value",
    },
    { title: "Birth Year", field: "birthYear", type: "numeric" },
    {
      title: "Birth Place",
      field: "birthCity",
      lookup: { 34: "İstanbul", 63: "Şanlıurfa" },
    },
  ]);

  const [data, setData] = useState([]);

  // useEffect(() => {
  //   const getDataEployee = axios.get("http://training-api.oceantech.com.vn/cms/employees/search")
  //   console.log(getDataEployee)
  // }, [])

  // const getDataAPI = () => {
  //   const getData = () => {
  //     axios.get("http://training-api.oceantech.com.vn/cms/employees/search").then((Response) => {
  //       console.log(Response)
  //     })
  //   }
  //   return getData();
  // }

  // useEffect(() => {
  //   axios.post('http://training-api.oceantech.com.vn/cms/employees/search',{})
  //     .then((Response) => {
  //       console.log('>>>>>>>>>>>> check res', Response)
  //     })
  //     .catch((error) => {
  //       console.log(error)
  //     });
  // }, [])
  useEffect(() => {
    getAllEmployee({ code: null, name: null }).then((res) =>{
      console.log(res.data.data);
      return res.data.data
    });
  }, []);

  return (
    <MaterialTable
      title="Thông tin nhân viên"
      columns={columns}
      data={data}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              setData([...data, newData]);

              resolve();
            }, 1000);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataUpdate = [...data];
              const index = oldData.tableData.id;
              dataUpdate[index] = newData;
              setData([...dataUpdate]);

              resolve();
            }, 1000);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataDelete = [...data];
              const index = oldData.tableData.id;
              dataDelete.splice(index, 1);
              setData([...dataDelete]);

              resolve();
            }, 1000);
          }),
      }}
    />
  );
}

export default EmployeeDialog;
