import React, { Component } from "react";
import {
  Dialog,
  Button,
  Grid,
  DialogActions,
  FormControl,
  Paper,
  DialogTitle,
  DialogContent,
} from "@material-ui/core";
// import Paper from '@material-ui/core/Paper'
import { ValidatorForm, TextValidator, TextField, MenuItem } from "react-material-ui-form-validator";
import Draggable from "react-draggable";
import NotificationPopup from "../Component/NotificationPopup/NotificationPopup";
import {
  saveEmployee,
  updateEmployee,
  checkCode,
  getCommune,
  getProvince,
  getDistrict,
} from "./EmployeeService";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure({
  autoClose: 2000,
  draggable: false,
  limit: 3,
});

function PaperComponent(props) {
  return (
    <Draggable
      handle="#draggable-dialog-title"
      cancel={'[class*="MuiDialogContent-root"]'}
    >
      <Paper {...props} />
    </Draggable>
  );
}

class EmployeeDialog extends Component {
  state = {
    id: "",
    name: "",
    code: "",
    phone: "",
    email: "",
    age: "",
    commune: "",
    district: "",
    province: "",
    listCommune: [],
    listDistrict: [],
    listProvince: [],
    type: "",
    shouldOpenNotificationPopup: false,
    Notification: "",
  };

  handleDialogClose = () => {
    this.setState({ shouldOpenNotificationPopup: false });
  };

  handleChange = (event, source) => {
    event.persist();
    if (source === "switch") {
      this.setState({ isActive: event.target.checked });
      return;
    }
    this.setState({
      [event.target.name]: event.target.value,
    });

  };

  // handleChange = (event, source) => {
  //   event.persist();
  //   if (source === "switch") {
  //     this.setState({ isActive: event.target.checked });
  //     return;
  //   }
  //   this.setState({
  //     [event.target.name]: event.target.value,
  //     [event.target.age]: event.target.value,
  //     [event.target.code]: event.target.value,
  //     [event.target.email]: event.target.value,
  //     [event.target.phone]: event.target.value,
  //     [event.target.commune]: event.target.value,
  //     [event.target.district]: event.target.value,
  //     [event.target.province]: event.target.value,
  //   });
  //   console.log("event: ", this.state);
  // };

  // handleFormSubmit = () => {
  //   let { id } = this.state;
  //   let { code } = this.state;
  //   let { name } = this.state;
  //   let { age } = this.state;
  //   let { email } = this.state;
  //   let { phone } = this.state;
  //   let { commune } = this.state;
  //   let { province } = this.state;
  //   let { district } = this.state;
  //   var { t } = this.props;
  //   checkCode(id, code, name, age, email, phone, commune, province, district).then((result) => {
  //     //Nếu trả về true là code đã được sử dụng
  //     if (result.data) {
  //       toast.warning(t("general.dupli_code"));
  //       // alert("Code đã được sử dụng");
  //     } else {
  //       //Nếu trả về false là code chưa sử dụng có thể dùng
  //       if (id) {
  //         updateEmployee({
  //           ...this.state,
  //         }).then(() => {
  //           toast.success(t("general.updateSuccess"));
  //           this.props.handleOKEditClose();
  //         });
  //       } else {
  //         saveEmployee({
  //           ...this.state,
  //         }).then(() => {
  //           toast.success(t("general.addSuccess"));
  //           this.props.handleOKEditClose();
  //         });
  //       }
  //     }
  //   });
  // };

  handleFormSubmit = () => {
    let {
      id,
      name,
      code,
      phone,
      email,
      age,
      commune,
      district,
      province,
    } = this.state;

    var { t, itemList } = this.props;

    const checkCode = (listItem, code) => {
      var listCode = [];
      listItem.forEach(element => {
        listCode.push(element.code)
      });
      return listCode.includes(code)
    }

    let check = checkCode(itemList, code)

    if (id) {
      updateEmployee({
        ...this.state,
      }).then(() => {
        toast.success(t("general.updateSuccess"));
        this.props.handleOKEditClose();
      });
    } else {
      if (check) {
        toast.warning(t("staff.noti.dupli_code"));
      } else {
        saveEmployee({
          name,
          code,
          phone,
          email,
          age,
          commune,
          district,
          province,
        }).then(() => {
          toast.success(t("general.addSuccess"));
          this.props.handleOKEditClose();
        });
      };
    };
  }

  getDistricts() {
    getDistrict({})
      .then(({ data }) => this.setState({ listDistrict: [...data.data] }))
      .catch(err => console.log(err))
    getCommune({})
      .then(({ data }) => this.setState({ listCommune: [...data.data] }))
      .catch(err => console.log(err))
    getProvince({})
      .then(({ data }) => this.setState({ listProvince: [...data.data] }))
      .catch(err => console.log(err))
  }

  componentWillMount() {
    //getUserById(this.props.uid).then(data => this.setState({ ...data.data }));
    // let { open, handleClose, item } = this.props;
    // this.setState({...item});
    this.getDistricts();
    let { open, handleClose, item, itemList } = this.props;
    this.setState({ ...item, itemList: itemList });


    //custom rule will have name 'isNameMatch'
    ValidatorForm.addValidationRule('isCodeMatch', (value) => {
      if (value.length > 10) {
        return false;
      }
      return true;
    });
  }


  componentWillUnmount() {
    // remove rule when it is not needed
    ValidatorForm.removeValidationRule('isCodeMatch');
  }

  render() {
    let {
      id,
      name,
      code,
      phone,
      email,
      age,
      commune,
      district,
      province,
      listCommune,
      listDistrict,
      listProvince,
      shouldOpenNotificationPopup,
    } = this.state;

    let { open, handleClose, handleOKEditClose, t, i18n } = this.props;
    return (
      <Dialog
        open={open}
        PaperComponent={PaperComponent}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle
          style={{ cursor: "move", paddingBottom: "0px" }}
          id="draggable-dialog-title"
        >
          <h4 className="">{id ? t("general.update") : t("general.addNew")}</h4>
        </DialogTitle>

        <ValidatorForm ref="form" onSubmit={this.handleFormSubmit}>
          <DialogContent>
            <Grid className="" container spacing={2}>
              {/* name */}
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 "
                  label={
                    <span>
                      <span style={{ color: "red" }}>*</span>
                      {t("general.name")}
                    </span>
                  }
                  onChange={this.handleChange}
                  type="text"
                  name="name"
                  value={name}
                  validators={["required"]}
                  errorMessages={[t("general.required")]}
                />
              </Grid>

                  {/* age */}
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 "
                  label={
                    <span>
                      <span style={{ color: "red" }}>*</span>
                      {t("staff.age")}
                    </span>
                  }
                  onChange={this.handleChange}
                  type="number"
                  name="age"
                  value={age}
                  validators={["required"]}
                  errorMessages={[t("general.required")]}
                />
              </Grid>
                  {/* code */}
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 "
                  label={
                    <span>
                      <span style={{ color: "red" }}>*</span>
                      {t("general.code")}
                    </span>
                  }
                  onChange={this.handleChange}
                  type="text"
                  name="code"
                  value={code}
                  validators={["required"]}
                  errorMessages={[t("general.required")]}
                />
              </Grid>
                  {/* mail */}
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 "
                  label={
                    <span>
                      <span style={{ color: "red" }}>*</span>
                      {t("general.email")}
                    </span>
                  }
                  onChange={this.handleChange}
                  type="email"
                  name="email"
                  value={email}
                  validators={["required"]}
                  errorMessages={[t("general.required")]}
                />
              </Grid>
                  {/* phone */}
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 "
                  label={
                    <span>
                      <span style={{ color: "red" }}>*</span>
                      {t("staff.phoneNumber")}
                    </span>
                  }
                  onChange={this.handleChange}
                  type="text"
                  name="phone"
                  value={phone}
                  validators={["required"]}
                  errorMessages={[t("general.required")]}
                />
              </Grid>

              <Grid item sm={4} xs={4}>
                <TextField
                  required
                  id="filled-select-province"
                  select
                  label="Tỉnh"
                  name="province"
                  value={province}
                  onChange={this.handleChange}
                  validators={["required"]}
                  helperText="Please select your province"
                  variant="filled"
                >
                  {listProvince.map((province) => (
                    <MenuItem key={province.id} value={province}>
                      {province.name}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item sm={4} xs={4}>
                <TextField
                  required
                  id="filled-select-district"
                  select
                  label="Huyện"
                  name="district"
                  value={district}
                  onChange={this.handleChange}
                  validators={["required"]}
                  helperText="Please select your district"
                  variant="filled"
                >
                  {listDistrict.map((district) => (
                    <MenuItem key={district.id} value={district}>
                      {district.name}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item sm={4} xs={4}>
                <TextField
                  required
                  id="filled-select-commune"
                  select
                  label="Xã"
                  name="commune"
                  value={commune}
                  onChange={this.handleChange}
                  validators={["required"]}
                  helperText="Please select your commune"
                  variant="filled"
                >
                  {listCommune.map((commune) => (
                    <MenuItem key={commune.id} value={commune}>
                      {commune.name}
                    </MenuItem>
                  ))}
                </TextField>

              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <div className="flex flex-space-between flex-middle mt-12">
              <Button
                variant="contained"
                className="mr-12"
                color="secondary"
                onClick={() => this.props.handleClose()}
              >
                {t("general.cancel")}
              </Button>
              <Button
                variant="contained"
                style={{ marginRight: "15px" }}
                // onSubmit={() => {this.props.handleFormSubmit()}}
                color="primary"
                type="submit"
              >
                {t("general.save")}
              </Button>
            </div>
          </DialogActions>
        </ValidatorForm>
      </Dialog>
    );
  }
}

export default EmployeeDialog;
