import React, { Component } from "react";
import {
  Grid,
  TextField,
  IconButton,
  Icon,
  Button,
  TableHead,
  TableCell,
  TableRow,
  Checkbox,
  TablePagination,
  Tooltip,
  FormControl,
  Input,
  InputAdornment,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import SearchIcon from "@material-ui/icons/Search";
import MaterialTable, {
  MTableToolbar,
  Chip,
  MTableBody,
  MTableHeader,
} from "material-table";
import { getAllEmployee,getItemById } from "./EmployeeService";
import { Breadcrumb, ConfirmationDialog } from "egret";
import { useTranslation, withTranslation, Trans } from "react-i18next";
import shortid from "shortid";
import { saveAs } from "file-saver";
import { useEffect, useState } from "react";
import axios from "axios";
import { withStyles } from "@material-ui/core/styles";
import { toast } from "react-toastify";
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3,
});


function MaterialButton(props) {
  const { t, i18n } = useTranslation();
  const item = props.item;
  return <div>
    <IconButton size="small" onClick={() => props.onSelect(item, 0)}>
      <Icon fontSize="small" color="primary">edit</Icon>
    </IconButton>
  </div>;
}

function EmployeeInfo(props) {

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getAllEmployee({ code: null, name: null })
      .then(res => {
        setPosts(res.data.data)
        console.log(res.data.data)
      })
      .catch(error => console.log(`Error: ${error}`));
  }, []);


    let columns = [
      {
        title: ("Actions"),
        field: "custom",
        align: "left",
        width: "250",
        render: rowData => <MaterialButton item={rowData}
          onSelect={(rowData, method) => {
            if (method === 0) {
              getItemById(rowData.id).then(({ data }) => {
                if (data.parent === null) {
                  data.parent = {};
                }
                this.setState({
                  item: data,
                  shouldOpenEditorDialog: true
                });
              })
            } else if (method === 1) {
              this.handleDelete(rowData.id);
            } else {
              alert('Call Selected Here:' + rowData.id);
            }
          }}
        />
      },
      { title: ("ID"), field: "id", width: "80" },
      { title: ("Name"), field: "name", width: "150" },
      { title: ("Age"), field: "age", width: "150" },
      { title: ("Code"), field: "code", width: "150" },
      { title: ("Province"), field: "province", width: "150" },
      { title: ("Email"), field: "email", align: "left", width: "150" },
      { title: ("Phone"), field: "phone", width: "150" },
    ];

  return (
    <MaterialTable
      title="Thông tin nhân viên"
      columns={columns}
      data={posts}
    />
  );
}

export default EmployeeInfo;
